import React, { useState } from 'react';
import {
  Container,
  Paper,
  TextField,
  Button,
  Typography,
  Grid,
  Select,
  MenuItem,
  FormControl,
  InputLabel,
  Box,
  CircularProgress,
  makeStyles,
  ThemeProvider,
  createTheme
} from '@mui/material';
import { useDropzone } from 'react-dropzone';
import axios from 'axios';

const theme = createTheme({
  palette: {
    primary: {
      main: '#2196f3',
    },
    secondary: {
      main: '#f50057',
    },
  },
});

const useStyles = makeStyles((theme) => ({
  root: {
    minHeight: '100vh',
    padding: theme.spacing(4),
    background: 'linear-gradient(135deg, #f5f7fa 0%, #c3cfe2 100%)',
  },
  paper: {
    padding: theme.spacing(4),
    borderRadius: '15px',
    boxShadow: '0 8px 32px rgba(0, 0, 0, 0.1)',
  },
  title: {
    marginBottom: theme.spacing(4),
    color: '#2c3e50',
    fontWeight: 'bold',
  },
  section: {
    marginBottom: theme.spacing(4),
  },
  dropzone: {
    border: '2px dashed #2196f3',
    borderRadius: '10px',
    padding: theme.spacing(3),
    textAlign: 'center',
    cursor: 'pointer',
    transition: 'all 0.3s ease',
    '&:hover': {
      borderColor: '#1976d2',
      backgroundColor: 'rgba(33, 150, 243, 0.04)',
    },
  },
  resultCard: {
    padding: theme.spacing(3),
    borderRadius: '10px',
    backgroundColor: '#f8f9fa',
    marginTop: theme.spacing(2),
  },
  button: {
    marginTop: theme.spacing(2),
    padding: theme.spacing(1.5),
    borderRadius: '8px',
    textTransform: 'none',
    fontWeight: 'bold',
  },
  errorText: {
    color: '#d32f2f',
    marginTop: theme.spacing(1),
  },
  successText: {
    color: '#2e7d32',
    marginTop: theme.spacing(1),
  },
}));

function MainApp() {
  const classes = useStyles();
  const [formData, setFormData] = useState({
    name: '',
    age: '',
    gender: '',
    symptoms: []
  });
  const [medicineImage, setMedicineImage] = useState(null);
  const [diagnosis, setDiagnosis] = useState(null);
  const [medicineInfo, setMedicineInfo] = useState(null);
  const [loading, setLoading] = useState(false);

  const onDrop = (acceptedFiles) => {
    setMedicineImage(acceptedFiles[0]);
  };

  const { getRootProps, getInputProps } = useDropzone({
    onDrop,
    accept: 'image/*',
    maxFiles: 1
  });

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleSymptomChange = (e) => {
    const symptoms = e.target.value.split(',').map(s => s.trim());
    setFormData(prev => ({
      ...prev,
      symptoms
    }));
  };

  const handleDiagnose = async () => {
    setLoading(true);
    try {
      const response = await axios.post('http://localhost:5000/api/diagnose', {
        symptoms: formData.symptoms
      });
      setDiagnosis(response.data);
    } catch (error) {
      console.error('Error during diagnosis:', error);
    }
    setLoading(false);
  };

  const handleMedicineAnalysis = async () => {
    if (!medicineImage) return;
    
    setLoading(true);
    setMedicineInfo(null);
    
    const formData = new FormData();
    formData.append('image', medicineImage);

    try {
      const response = await axios.post('http://localhost:5000/api/analyze-medicine', formData, {
        headers: {
          'Content-Type': 'multipart/form-data'
        }
      });
      
      if (response.data.error) {
        setMedicineInfo({
          error: response.data.error,
          ocrText: response.data.ocr_text
        });
      } else {
        setMedicineInfo(response.data);
      }
    } catch (error) {
      console.error('Error during medicine analysis:', error);
      setMedicineInfo({
        error: error.response?.data?.error || 'Failed to analyze medicine image'
      });
    }
    setLoading(false);
  };

  return (
    <ThemeProvider theme={theme}>
      <div className={classes.root}>
        <Container maxWidth="md">
          <Paper className={classes.paper}>
            <Typography variant="h4" className={classes.title}>
              MedNexus - AI Medical Assistant
            </Typography>

            <Grid container spacing={4}>
              <Grid item xs={12}>
                <Typography variant="h6" className={classes.section}>
                  Patient Information
                </Typography>
                <Grid container spacing={3}>
                  <Grid item xs={12}>
                    <TextField
                      fullWidth
                      label="Patient Name"
                      name="name"
                      value={formData.name}
                      onChange={handleInputChange}
                      variant="outlined"
                    />
                  </Grid>

                  <Grid item xs={6}>
                    <TextField
                      fullWidth
                      label="Age"
                      name="age"
                      type="number"
                      value={formData.age}
                      onChange={handleInputChange}
                      variant="outlined"
                    />
                  </Grid>

                  <Grid item xs={6}>
                    <FormControl fullWidth variant="outlined">
                      <InputLabel>Gender</InputLabel>
                      <Select
                        name="gender"
                        value={formData.gender}
                        onChange={handleInputChange}
                        label="Gender"
                      >
                        <MenuItem value="male">Male</MenuItem>
                        <MenuItem value="female">Female</MenuItem>
                        <MenuItem value="other">Other</MenuItem>
                      </Select>
                    </FormControl>
                  </Grid>
                </Grid>
              </Grid>

              <Grid item xs={12}>
                <Typography variant="h6" className={classes.section}>
                  Symptoms
                </Typography>
                <TextField
                  fullWidth
                  label="Enter your symptoms (comma-separated)"
                  name="symptoms"
                  value={formData.symptoms.join(', ')}
                  onChange={handleSymptomChange}
                  multiline
                  rows={4}
                  variant="outlined"
                  helperText="Example: fever, cough, headache"
                />
                <Button
                  variant="contained"
                  color="primary"
                  onClick={handleDiagnose}
                  disabled={loading}
                  className={classes.button}
                  fullWidth
                >
                  Get Diagnosis
                </Button>
              </Grid>

              {diagnosis && (
                <Grid item xs={12}>
                  <Paper className={classes.resultCard}>
                    <Typography variant="h6" className={classes.successText}>
                      Diagnosis Results
                    </Typography>
                    <Typography variant="body1">
                      <strong>Diagnosis:</strong> {diagnosis.diagnosis}
                    </Typography>
                    <Typography variant="body1">
                      <strong>Recommended Medicines:</strong> {diagnosis.medicines.join(', ')}
                    </Typography>
                    <Typography variant="body1">
                      <strong>Precautions:</strong> {diagnosis.precautions}
                    </Typography>
                  </Paper>
                </Grid>
              )}

              <Grid item xs={12}>
                <Typography variant="h6" className={classes.section}>
                  Medicine Analysis
                </Typography>
                <Box
                  {...getRootProps()}
                  className={classes.dropzone}
                >
                  <input {...getInputProps()} />
                  {medicineImage ? (
                    <Typography variant="body1">
                      Selected: {medicineImage.name}
                    </Typography>
                  ) : (
                    <Typography variant="body1">
                      Drag & drop a medicine image here, or click to select
                    </Typography>
                  )}
                </Box>
                <Button
                  variant="contained"
                  color="secondary"
                  onClick={handleMedicineAnalysis}
                  disabled={!medicineImage || loading}
                  className={classes.button}
                  fullWidth
                >
                  Analyze Medicine
                </Button>
              </Grid>

              {medicineInfo && (
                <Grid item xs={12}>
                  <Paper className={classes.resultCard}>
                    {medicineInfo.error ? (
                      <>
                        <Typography variant="h6" className={classes.errorText}>
                          Error
                        </Typography>
                        <Typography variant="body1" className={classes.errorText}>
                          {medicineInfo.error}
                        </Typography>
                        {medicineInfo.ocrText && (
                          <>
                            <Typography variant="subtitle2" style={{ marginTop: '1rem' }}>
                              OCR Text:
                            </Typography>
                            <Typography variant="body2" style={{ fontFamily: 'monospace' }}>
                              {medicineInfo.ocrText}
                            </Typography>
                          </>
                        )}
                      </>
                    ) : (
                      <>
                        <Typography variant="h6" className={classes.successText}>
                          Medicine Information
                        </Typography>
                        <Typography variant="body1">
                          <strong>Name:</strong> {medicineInfo.name}
                        </Typography>
                        <Typography variant="body1">
                          <strong>Usage:</strong> {medicineInfo.usage}
                        </Typography>
                        <Typography variant="body1">
                          <strong>Dosage:</strong> {medicineInfo.dosage}
                        </Typography>
                        <Typography variant="body1">
                          <strong>Side Effects:</strong> {medicineInfo.side_effects}
                        </Typography>
                      </>
                    )}
                  </Paper>
                </Grid>
              )}
            </Grid>

            {loading && (
              <Box display="flex" justifyContent="center" mt={4}>
                <CircularProgress />
              </Box>
            )}
          </Paper>
        </Container>
      </div>
    </ThemeProvider>
  );
}

export default MainApp;
