import React, { useState } from 'react';
import {
  Container,
  Paper,
  TextField,
  Button,
  Typography,
  Grid,
  Select,
  MenuItem,
  FormControl,
  InputLabel,
  Box,
  CircularProgress
} from '@material-ui/core';
import { useDropzone } from 'react-dropzone';
import axios from 'axios';

function App() {
  const [formData, setFormData] = useState({
    name: '',
    age: '',
    gender: '',
    symptoms: []
  });
  const [medicineImage, setMedicineImage] = useState(null);
  const [diagnosis, setDiagnosis] = useState(null);
  const [medicineInfo, setMedicineInfo] = useState(null);
  const [loading, setLoading] = useState(false);

  const onDrop = (acceptedFiles) => {
    setMedicineImage(acceptedFiles[0]);
  };

  const { getRootProps, getInputProps } = useDropzone({
    onDrop,
    accept: 'image/*',
    maxFiles: 1
  });

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleSymptomChange = (e) => {
    const symptoms = e.target.value.split(',').map(s => s.trim());
    setFormData(prev => ({
      ...prev,
      symptoms
    }));
  };

  const handleDiagnose = async () => {
    setLoading(true);
    try {
      const response = await axios.post('http://localhost:5000/api/diagnose', {
        symptoms: formData.symptoms
      });
      setDiagnosis(response.data);
    } catch (error) {
      console.error('Error during diagnosis:', error);
    }
    setLoading(false);
  };

  const handleMedicineAnalysis = async () => {
    if (!medicineImage) return;
    
    setLoading(true);
    setMedicineInfo(null); // Reset previous results
    
    const formData = new FormData();
    formData.append('image', medicineImage);

    try {
      const response = await axios.post('http://localhost:5000/api/analyze-medicine', formData, {
        headers: {
          'Content-Type': 'multipart/form-data'
        }
      });
      
      if (response.data.error) {
        // Handle error response
        setMedicineInfo({
          error: response.data.error,
          ocrText: response.data.ocr_text
        });
      } else {
        setMedicineInfo(response.data);
      }
    } catch (error) {
      console.error('Error during medicine analysis:', error);
      setMedicineInfo({
        error: error.response?.data?.error || 'Failed to analyze medicine image'
      });
    }
    setLoading(false);
  };

  return (
    <Container maxWidth="md">
      <Paper elevation={3} style={{ padding: '2rem', marginTop: '2rem' }}>
        <Typography variant="h4" gutterBottom>
          MedNexus - AI Medical Assistant
        </Typography>

        <Grid container spacing={3}>
          <Grid item xs={12}>
            <TextField
              fullWidth
              label="Patient Name"
              name="name"
              value={formData.name}
              onChange={handleInputChange}
            />
          </Grid>

          <Grid item xs={6}>
            <TextField
              fullWidth
              label="Age"
              name="age"
              type="number"
              value={formData.age}
              onChange={handleInputChange}
            />
          </Grid>

          <Grid item xs={6}>
            <FormControl fullWidth>
              <InputLabel>Gender</InputLabel>
              <Select
                name="gender"
                value={formData.gender}
                onChange={handleInputChange}
              >
                <MenuItem value="male">Male</MenuItem>
                <MenuItem value="female">Female</MenuItem>
                <MenuItem value="other">Other</MenuItem>
              </Select>
            </FormControl>
          </Grid>

          <Grid item xs={12}>
            <TextField
              fullWidth
              label="Symptoms (comma-separated)"
              name="symptoms"
              value={formData.symptoms.join(', ')}
              onChange={handleSymptomChange}
              multiline
              rows={4}
            />
          </Grid>

          <Grid item xs={12}>
            <Button
              variant="contained"
              color="primary"
              onClick={handleDiagnose}
              disabled={loading}
            >
              Get Diagnosis
            </Button>
          </Grid>

          {diagnosis && (
            <Grid item xs={12}>
              <Paper elevation={2} style={{ padding: '1rem' }}>
                <Typography variant="h6">Diagnosis Results</Typography>
                <Typography>Diagnosis: {diagnosis.diagnosis}</Typography>
                <Typography>Medicines: {diagnosis.medicines.join(', ')}</Typography>
                <Typography>Precautions: {diagnosis.precautions}</Typography>
              </Paper>
            </Grid>
          )}

          <Grid item xs={12}>
            <Typography variant="h6" gutterBottom>
              Medicine Image Analysis
            </Typography>
            <Box
              {...getRootProps()}
              style={{
                border: '2px dashed #ccc',
                padding: '2rem',
                textAlign: 'center',
                cursor: 'pointer'
              }}
            >
              <input {...getInputProps()} />
              {medicineImage ? (
                <Typography>{medicineImage.name}</Typography>
              ) : (
                <Typography>Drag & drop a medicine image here, or click to select</Typography>
              )}
            </Box>
          </Grid>

          <Grid item xs={12}>
            <Button
              variant="contained"
              color="secondary"
              onClick={handleMedicineAnalysis}
              disabled={!medicineImage || loading}
            >
              Analyze Medicine
            </Button>
          </Grid>

          {medicineInfo && (
            <Grid item xs={12}>
              <Paper elevation={2} style={{ padding: '1rem' }}>
                {medicineInfo.error ? (
                  <>
                    <Typography variant="h6" color="error">Error</Typography>
                    <Typography color="error">{medicineInfo.error}</Typography>
                    {medicineInfo.ocrText && (
                      <>
                        <Typography variant="subtitle2" style={{ marginTop: '1rem' }}>OCR Text:</Typography>
                        <Typography variant="body2" style={{ fontFamily: 'monospace' }}>
                          {medicineInfo.ocrText}
                        </Typography>
                      </>
                    )}
                  </>
                ) : (
                  <>
                    <Typography variant="h6">Medicine Information</Typography>
                    <Typography>Name: {medicineInfo.name}</Typography>
                    <Typography>Usage: {medicineInfo.usage}</Typography>
                    <Typography>Dosage: {medicineInfo.dosage}</Typography>
                    <Typography>Side Effects: {medicineInfo.side_effects}</Typography>
                  </>
                )}
              </Paper>
            </Grid>
          )}
        </Grid>

        {loading && (
          <Box display="flex" justifyContent="center" mt={2}>
            <CircularProgress />
          </Box>
        )}
      </Paper>
    </Container>
  );
}

export default App;
