import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './MainApp.css';

const API_BASE_URL = 'http://localhost:8000';

function MainApp() {
  const [symptoms, setSymptoms] = useState([]);
  const [selectedSymptoms, setSelectedSymptoms] = useState([]);
  const [diagnosis, setDiagnosis] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [medicineAnalysis, setMedicineAnalysis] = useState(null);
  const [medicineLoading, setMedicineLoading] = useState(false);
  const [medicineError, setMedicineError] = useState(null);

  useEffect(() => {
    fetchSymptoms();
  }, []);

  const fetchSymptoms = async () => {
    try {
      const response = await axios.get(`${API_BASE_URL}/symptoms`);
      setSymptoms(response.data);
    } catch (err) {
      setError('Failed to fetch symptoms. Please try again later.');
    }
  };

  const toggleSymptom = (symptom) => {
    setSelectedSymptoms(prev => 
      prev.includes(symptom)
        ? prev.filter(s => s !== symptom)
        : [...prev, symptom]
    );
  };

  const handleDiagnosis = async () => {
    if (selectedSymptoms.length === 0) {
      setError('Please select at least one symptom');
      return;
    }

    setLoading(true);
    setError(null);

    try {
      const response = await axios.post(`${API_BASE_URL}/diagnose`, {
        symptoms: selectedSymptoms
      });
      setDiagnosis(response.data);
    } catch (err) {
      setError('Failed to get diagnosis. Please try again later.');
    } finally {
      setLoading(false);
    }
  };

  const analyzeMedicine = async (file) => {
    setMedicineLoading(true);
    setMedicineError(null);
    setMedicineAnalysis(null);

    try {
      if (!file) {
        throw new Error('No file selected');
      }

      if (!file.type.startsWith('image/')) {
        throw new Error('Please upload an image file');
      }

      const formData = new FormData();
      formData.append('file', file);

      const response = await axios.post(`${API_BASE_URL}/analyze_medicine`, formData, {
        headers: {
          'Content-Type': 'multipart/form-data'
        }
      });

      setMedicineAnalysis(response.data);
    } catch (err) {
      console.error('Medicine analysis error:', err);
      setMedicineError(err.response?.data?.detail || 'Failed to analyze medicine image. Please try again.');
    } finally {
      setMedicineLoading(false);
    }
  };

  const handleImageUpload = (event) => {
    const file = event.target.files[0];
    if (file) {
      analyzeMedicine(file);
    }
  };

  return (
    <div className="main-app">
      <h1>Medical Diagnosis System</h1>

      <div className="symptoms-section">
        <h2>Select Symptoms</h2>
        <div className="symptoms-grid">
          {symptoms.map(symptom => (
            <button
              key={symptom}
              className={`symptom-button ${selectedSymptoms.includes(symptom) ? 'selected' : ''}`}
              onClick={() => toggleSymptom(symptom)}
            >
              {symptom}
            </button>
          ))}
        </div>
      </div>

      <button 
        className="diagnose-button"
        onClick={handleDiagnosis}
        disabled={loading}
      >
        {loading ? 'Diagnosing...' : 'Get Diagnosis'}
      </button>

      {error && <div className="error-message">{error}</div>}

      {diagnosis && (
        <div className="results-section">
          <h2>Diagnosis Results</h2>

          <div className="symptoms-details">
            <h3>Selected Symptoms</h3>
            {diagnosis.symptoms.map(symptom => (
              <div key={symptom.name} className="symptom-detail">
                <h4>{symptom.name}</h4>
                <p><strong>Description:</strong> {symptom.description}</p>
                <p><strong>Severity:</strong> {symptom.severity}</p>
                <p><strong>Contagious:</strong> {symptom.contagious ? 'Yes' : 'No'}</p>
              </div>
            ))}
          </div>

          <div className="disease-predictions">
            <h3>Possible Diseases</h3>
            {diagnosis.predictions.map((prediction, index) => (
              <div key={index} className="disease-prediction">
                <h4>{prediction.disease}</h4>
                <p>Confidence: {(prediction.probability * 100).toFixed(2)}%</p>
              </div>
            ))}
          </div>
        </div>
      )}

      <div className="medicine-analysis-section">
        <h2>Medicine Analysis</h2>
        <input
          type="file"
          accept="image/*"
          onChange={handleImageUpload}
          className="image-upload"
        />

        {medicineLoading && <div className="loading">Analyzing image...</div>}
        {medicineError && <div className="error">{medicineError}</div>}

        {medicineAnalysis && (
          <div className="medicine-results">
            {medicineAnalysis.status === 'success' ? (
              <>
                <h3>Found Medicines</h3>
                {medicineAnalysis.medicines.map((medicine, index) => (
                  <div key={index} className="medicine-card">
                    <h4>{medicine.name}</h4>
                    <p><strong>Usage:</strong> {medicine.usage}</p>
                    <p><strong>Dosage:</strong> {medicine.dosage}</p>
                    <p><strong>Side Effects:</strong> {medicine.side_effects}</p>
                  </div>
                ))}
              </>
            ) : (
              <div className="not-found">
                <p>{medicineAnalysis.message}</p>
                <p>Extracted text: {medicineAnalysis.extracted_text}</p>
              </div>
            )}
          </div>
        )}
      </div>
    </div>
  );
}

export default MainApp;
