import React from 'react';
import { useNavigate } from 'react-router-dom';
import { styled } from '@mui/material/styles';
import {
  Container,
  Typography,
  Button,
  Grid,
} from '@mui/material';

const Root = styled('div')(({ theme }) => ({
  minHeight: '100vh',
  background: 'linear-gradient(135deg, #1a2a6c 0%, #b21f1f 50%, #fdbb2d 100%)',
  display: 'flex',
  alignItems: 'center',
  justifyContent: 'center',
  color: 'white',
}));

const StyledContainer = styled(Container)(({ theme }) => ({
  textAlign: 'center',
  padding: theme.spacing(4),
}));

const Title = styled(Typography)(({ theme }) => ({
  fontSize: '4.5rem',
  fontWeight: 'bold',
  marginBottom: theme.spacing(2),
  textShadow: '2px 2px 4px rgba(0,0,0,0.3)',
  animation: 'fadeIn 1.5s ease-in',
}));

const Subtitle = styled(Typography)(({ theme }) => ({
  fontSize: '1.5rem',
  marginBottom: theme.spacing(4),
  opacity: 0.9,
  animation: 'fadeIn 2s ease-in',
}));

const StyledButton = styled(Button)(({ theme }) => ({
  padding: theme.spacing(2, 4),
  fontSize: '1.2rem',
  borderRadius: '30px',
  background: 'rgba(255, 255, 255, 0.2)',
  backdropFilter: 'blur(10px)',
  border: '2px solid rgba(255, 255, 255, 0.3)',
  color: 'white',
  transition: 'all 0.3s ease',
  '&:hover': {
    background: 'rgba(255, 255, 255, 0.3)',
    transform: 'translateY(-3px)',
    boxShadow: '0 10px 20px rgba(0,0,0,0.2)',
  },
}));

const FeaturesContainer = styled(Grid)(({ theme }) => ({
  marginTop: theme.spacing(8),
  padding: theme.spacing(4),
  background: 'rgba(255, 255, 255, 0.1)',
  borderRadius: '20px',
  backdropFilter: 'blur(10px)',
}));

const FeatureItem = styled(Grid)(({ theme }) => ({
  padding: theme.spacing(2),
  textAlign: 'center',
}));

const keyframes = `
  @keyframes fadeIn {
    from {
      opacity: 0;
      transform: translateY(20px);
    }
    to {
      opacity: 1;
      transform: translateY(0);
    }
  }
`;

function LandingPage() {
  const navigate = useNavigate();

  const handleGetStarted = () => {
    navigate('/app');
  };

  return (
    <Root>
      <style>{keyframes}</style>
      <StyledContainer>
        <Title variant="h1">
          MedNexus
        </Title>
        <Subtitle variant="h4">
          "Connecting Innovation, Insight, and Impact in Modern Healthcare"
        </Subtitle>
        <StyledButton
          variant="contained"
          onClick={handleGetStarted}
        >
          Get Started
        </StyledButton>

        <FeaturesContainer container spacing={4}>
          <FeatureItem item xs={12} md={4}>
            <Typography variant="h5" gutterBottom>
              AI-Powered Diagnosis
            </Typography>
            <Typography variant="body1">
              Accurate and instant medical diagnosis using advanced AI algorithms
            </Typography>
          </FeatureItem>
          <FeatureItem item xs={12} md={4}>
            <Typography variant="h5" gutterBottom>
              Medicine Recognition
            </Typography>
            <Typography variant="body1">
              Smart medicine identification and information retrieval
            </Typography>
          </FeatureItem>
          <FeatureItem item xs={12} md={4}>
            <Typography variant="h5" gutterBottom>
              Personalized Care
            </Typography>
            <Typography variant="body1">
              Tailored treatment recommendations and precautions
            </Typography>
          </FeatureItem>
        </FeaturesContainer>
      </StyledContainer>
    </Root>
  );
}

export default LandingPage;
