import React from 'react';
import { useNavigate } from 'react-router-dom';
import { makeStyles } from '@material-ui/core/styles';
import {
  Container,
  Typography,
  Button,
  Box,
  Grid,
} from '@material-ui/core';

const useStyles = makeStyles((theme) => ({
  root: {
    minHeight: '100vh',
    background: 'linear-gradient(135deg, #1a2a6c 0%, #b21f1f 50%, #fdbb2d 100%)',
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center',
    color: 'white',
  },
  container: {
    textAlign: 'center',
    padding: theme.spacing(4),
  },
  title: {
    fontSize: '4.5rem',
    fontWeight: 'bold',
    marginBottom: theme.spacing(2),
    textShadow: '2px 2px 4px rgba(0,0,0,0.3)',
    animation: '$fadeIn 1.5s ease-in',
  },
  subtitle: {
    fontSize: '1.5rem',
    marginBottom: theme.spacing(4),
    opacity: 0.9,
    animation: '$fadeIn 2s ease-in',
  },
  button: {
    padding: theme.spacing(2, 4),
    fontSize: '1.2rem',
    borderRadius: '30px',
    background: 'rgba(255, 255, 255, 0.2)',
    backdropFilter: 'blur(10px)',
    border: '2px solid rgba(255, 255, 255, 0.3)',
    color: 'white',
    transition: 'all 0.3s ease',
    '&:hover': {
      background: 'rgba(255, 255, 255, 0.3)',
      transform: 'translateY(-3px)',
      boxShadow: '0 10px 20px rgba(0,0,0,0.2)',
    },
  },
  features: {
    marginTop: theme.spacing(8),
    padding: theme.spacing(4),
    background: 'rgba(255, 255, 255, 0.1)',
    borderRadius: '20px',
    backdropFilter: 'blur(10px)',
  },
  featureItem: {
    padding: theme.spacing(2),
    textAlign: 'center',
  },
  '@keyframes fadeIn': {
    from: {
      opacity: 0,
      transform: 'translateY(20px)',
    },
    to: {
      opacity: 1,
      transform: 'translateY(0)',
    },
  },
}));

function LandingPage() {
  const classes = useStyles();
  const navigate = useNavigate();

  const handleGetStarted = () => {
    navigate('/app');
  };

  return (
    <div className={classes.root}>
      <Container className={classes.container}>
        <Typography variant="h1" className={classes.title}>
          MedNexus
        </Typography>
        <Typography variant="h4" className={classes.subtitle}>
          "Connecting Innovation, Insight, and Impact in Modern Healthcare"
        </Typography>
        <Button
          variant="contained"
          className={classes.button}
          onClick={handleGetStarted}
        >
          Get Started
        </Button>

        <Grid container spacing={4} className={classes.features}>
          <Grid item xs={12} md={4} className={classes.featureItem}>
            <Typography variant="h5" gutterBottom>
              AI-Powered Diagnosis
            </Typography>
            <Typography variant="body1">
              Accurate and instant medical diagnosis using advanced AI algorithms
            </Typography>
          </Grid>
          <Grid item xs={12} md={4} className={classes.featureItem}>
            <Typography variant="h5" gutterBottom>
              Medicine Recognition
            </Typography>
            <Typography variant="body1">
              Smart medicine identification and information retrieval
            </Typography>
          </Grid>
          <Grid item xs={12} md={4} className={classes.featureItem}>
            <Typography variant="h5" gutterBottom>
              Personalized Care
            </Typography>
            <Typography variant="body1">
              Tailored treatment recommendations and precautions
            </Typography>
          </Grid>
        </Grid>
      </Container>
    </div>
  );
}

export default LandingPage;
