import React, { useState, useEffect } from 'react';
import axios from 'axios';
import '../styles/UserInputPage.css';

const API_BASE_URL = 'http://localhost:8000';

const UserInputPage = () => {
  const [symptoms, setSymptoms] = useState([]);
  const [selectedSymptoms, setSelectedSymptoms] = useState([]);
  const [diagnosis, setDiagnosis] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  useEffect(() => {
    fetchSymptoms();
  }, []);

  const fetchSymptoms = async () => {
    try {
      const response = await axios.get(`${API_BASE_URL}/symptoms`);
      setSymptoms(response.data);
    } catch (err) {
      setError('Failed to fetch symptoms. Please try again later.');
    }
  };

  const toggleSymptom = (symptom) => {
    setSelectedSymptoms(prev => {
      if (prev.includes(symptom)) {
        return prev.filter(s => s !== symptom);
      } else {
        return [...prev, symptom];
      }
    });
  };

  const handleAnalyze = async () => {
    if (selectedSymptoms.length === 0) {
      setError('Please select at least one symptom');
      return;
    }

    setLoading(true);
    setError('');
    setDiagnosis(null);

    try {
      const response = await axios.post(`${API_BASE_URL}/diagnose`, {
        symptoms: selectedSymptoms
      });
      setDiagnosis(response.data);
    } catch (err) {
      setError('Failed to get diagnosis. Please try again later.');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="user-input-page">
      <div className="container">
        <h1 className="page-title">Select Your Symptoms</h1>
        
        {error && <div className="error-message">{error}</div>}
        
        <div className="symptoms-container">
          {symptoms.map((symptom) => (
            <button
              key={symptom}
              className={`symptom-button ${selectedSymptoms.includes(symptom) ? 'selected' : ''}`}
              onClick={() => toggleSymptom(symptom)}
            >
              {symptom}
            </button>
          ))}
        </div>

        <button
          className="analyze-button"
          onClick={handleAnalyze}
          disabled={loading || selectedSymptoms.length === 0}
        >
          {loading ? 'Analyzing...' : 'Analyze Symptoms'}
        </button>

        {loading && <div className="loading">Processing your symptoms...</div>}

        {diagnosis && (
          <div className="diagnosis-result">
            <h2>Diagnosis Results</h2>
            <p><strong>Primary Diagnosis:</strong> {diagnosis.primary_diagnosis}</p>
            <p><strong>Confidence:</strong> {diagnosis.confidence}%</p>
            
            <h3>Possible Diagnoses:</h3>
            <ul>
              {diagnosis.possible_diagnoses.map((item, index) => (
                <li key={index}>
                  {item.disease} ({item.confidence}%)
                </li>
              ))}
            </ul>

            <h3>Recommended Medicines:</h3>
            <ul>
              {diagnosis.recommended_medicines.map((medicine, index) => (
                <li key={index}>
                  {medicine.name} - {medicine.usage}
                </li>
              ))}
            </ul>
          </div>
        )}
      </div>
    </div>
  );
};

export default UserInputPage;
