import React, { useState, useRef } from 'react';
import axios from 'axios';
import '../styles/UserInputPage.css';

const API_BASE_URL = 'http://localhost:8000';

const UserInputPage = () => {
  const [patientInfo, setPatientInfo] = useState({
    name: '',
    age: '',
    gender: '',
    symptoms: ''
  });
  const [diagnosis, setDiagnosis] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [scannedMedicine, setScannedMedicine] = useState(null);
  const fileInputRef = useRef(null);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setPatientInfo(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleGenderSelect = (gender) => {
    setPatientInfo(prev => ({
      ...prev,
      gender
    }));
  };

  const handleImageUpload = async (e) => {
    const file = e.target.files[0];
    if (!file) return;

    const formData = new FormData();
    formData.append('image', file);

    try {
      setLoading(true);
      const response = await axios.post(`${API_BASE_URL}/analyze-medicine`, formData, {
        headers: {
          'Content-Type': 'multipart/form-data'
        }
      });
      setScannedMedicine(response.data);
    } catch (err) {
      console.error('Error analyzing medicine:', err);
      setError('Failed to analyze medicine image. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  const handleAnalyze = async () => {
    if (!patientInfo.name || !patientInfo.age || !patientInfo.gender || !patientInfo.symptoms) {
      setError('Please fill in all fields');
      return;
    }

    setLoading(true);
    setError('');
    setDiagnosis(null);

    try {
      const symptomsList = patientInfo.symptoms.split(',').map(s => s.trim());
      const response = await axios.post(`${API_BASE_URL}/diagnose`, {
        patientInfo: {
          name: patientInfo.name,
          age: patientInfo.age,
          gender: patientInfo.gender
        },
        symptoms: symptomsList
      });
      setDiagnosis(response.data);
    } catch (err) {
      console.error('Error getting diagnosis:', err);
      setError('Failed to get diagnosis. Please try again later.');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="user-input-page">
      <div className="container">
        <h1 className="page-title">Patient Information</h1>

        {error && <div className="error-message">{error}</div>}

        <div className="form-group">
          <label htmlFor="name">Patient Name:</label>
          <input
            type="text"
            id="name"
            name="name"
            value={patientInfo.name}
            onChange={handleInputChange}
            placeholder="Enter patient name"
          />
        </div>

        <div className="form-group">
          <label htmlFor="age">Age:</label>
          <input
            type="number"
            id="age"
            name="age"
            value={patientInfo.age}
            onChange={handleInputChange}
            placeholder="Enter age"
          />
        </div>

        <div className="form-group">
          <label>Select Gender:</label>
          <div className="gender-options">
            <div
              className={`gender-option ${patientInfo.gender === 'male' ? 'selected' : ''}`}
              onClick={() => handleGenderSelect('male')}
            >
              Male
            </div>
            <div
              className={`gender-option ${patientInfo.gender === 'female' ? 'selected' : ''}`}
              onClick={() => handleGenderSelect('female')}
            >
              Female
            </div>
            <div
              className={`gender-option ${patientInfo.gender === 'other' ? 'selected' : ''}`}
              onClick={() => handleGenderSelect('other')}
            >
              Other
            </div>
          </div>
        </div>

        <div className="form-group">
          <label htmlFor="symptoms">Enter your symptoms (comma-separated)</label>
          <textarea
            id="symptoms"
            name="symptoms"
            value={patientInfo.symptoms}
            onChange={handleInputChange}
            className="symptoms-input"
            placeholder="Example: fever, cough, headache"
          />
        </div>

        <button
          className="analyze-button"
          onClick={handleAnalyze}
          disabled={loading}
        >
          {loading ? 'Analyzing...' : 'Get Diagnosis'}
        </button>

        <div className="medicine-analysis">
          <h2>Medicine Analysis</h2>
          <div className="scanner-section">
            <div 
              className="scanner-container"
              onClick={() => fileInputRef.current?.click()}
            >
              <div className="scanner-icon">ðŸ“·</div>
              <div className="scanner-text">
                Click to scan medicine or upload image
              </div>
              <input
                type="file"
                ref={fileInputRef}
                className="scanner-input"
                accept="image/*"
                onChange={handleImageUpload}
              />
            </div>
          </div>

          {scannedMedicine && (
            <div className="medicine-info">
              <h3>Found Medicines</h3>
              <div className="medicine-details">
                <p><strong>Name:</strong> {scannedMedicine.name || 'Unknown Medicine'}</p>
                <p><strong>Usage:</strong> {scannedMedicine.usage || 'Not specified'}</p>
                <p><strong>Dosage:</strong> {scannedMedicine.dosage || 'Not specified'}</p>
                <p><strong>Side Effects:</strong> {scannedMedicine.side_effects || 'Not specified'}</p>
              </div>
            </div>
          )}
        </div>

        {diagnosis && diagnosis.recommended_medicines && (
          <div className="diagnosis-result">
            <div className="medicine-info">
              <h3>Recommended Medicines</h3>
              {Array.isArray(diagnosis.recommended_medicines) && diagnosis.recommended_medicines.map((medicine, index) => (
                <div key={index} className="medicine-details">
                  <p><strong>Name:</strong> {medicine.name || 'Unknown Medicine'}</p>
                  <p><strong>Usage:</strong> {medicine.usage || 'Not specified'}</p>
                  <p><strong>Dosage:</strong> {medicine.dosage || 'Not specified'}</p>
                  <p><strong>Side Effects:</strong> {medicine.side_effects || 'Not specified'}</p>
                </div>
              ))}
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default UserInputPage;
